mapper.select:
SELECT S.*, O.OS_NAME, CAST(AVG(1 - CPU.CPU_IDLE)*100 AS DECIMAL(10, 2)) AS CPU_USER_PERCENT, CAST(AVG(MEMORY.MEN_USED_PERCENT)*100 AS DECIMAL(10, 2)) AS MEN_USED_PERCENT, NETCARD.DOWNLOAD_BPS, NETCARD.UPLOAD_BPS, AVERAGE.FIFTEEN AS LOAD_AVERAGE FROM MONITOR_SERVER S LEFT JOIN MONITOR_SERVER_OS O ON O.IP =S.IP LEFT JOIN MONITOR_SERVER_CPU CPU ON CPU.IP = S.IP LEFT JOIN MONITOR_SERVER_MEMORY MEMORY ON MEMORY.IP = S.IP LEFT JOIN (SELECT IP, ADDRESS, MAX( DOWNLOAD_BPS ) AS DOWNLOAD_BPS, MAX( UPLOAD_BPS ) AS UPLOAD_BPS FROM MONITOR_SERVER_NETCARD GROUP BY IP, ADDRESS ) NETCARD ON (NETCARD.IP = S.IP AND NETCARD.ADDRESS = S.IP) LEFT JOIN MONITOR_SERVER_LOAD_AVERAGE AVERAGE ON AVERAGE.IP=S.IP
AND S.IP LIKE concat('%',#{criteria.ip},'%')
AND S.SERVER_NAME LIKE concat('%',#{criteria.serverName},'%')
AND S.IS_ONLINE IS NULL
AND S.IS_ONLINE=#{criteria.isOnline}
AND S.MONITOR_ENV = #{criteria.monitorEnv}
AND S.MONITOR_GROUP = #{criteria.monitorGroup}
AND O.OS_NAME LIKE concat('%',#{criteria.osName},'%')
AND S.SERVER_SUMMARY LIKE concat('%',#{criteria.serverSummary},'%')
GROUP BY S.ID, S.IP, S.SERVER_NAME, S.SERVER_SUMMARY, S.IS_ONLINE, S.CONN_FREQUENCY, S.INSERT_TIME, S.UPDATE_TIME, O.OS_NAME, NETCARD.DOWNLOAD_BPS, NETCARD.UPLOAD_BPS, AVERAGE.FIFTEEN ORDER BY S.ID
